import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaChartLine,
  FaWallet,
  FaUser,
  FaHome,
  FaChartPie,
  FaUndo,
  FaReceipt,
} from 'react-icons/fa';

const SHEET_ID = '1eSQ0m5OCLYud_JBhkW26v-geKuCMOxEM-FCjIBxuuAw';
const API_KEY = 'AIzaSyAnWS12-i37cqFBWIqfHA8XASrU-0ahD80';
const RANGE = 'Sheet1!A2:G2';

function App() {
  const [data, setData] = useState({
    currentValue: '-',
    totalReturns: '-',
    returnsPercent: '-',
    invested: '-',
    xirr: '-',
    dayReturn: '-',
    dayReturnPercent: '-',
  });

  const [activeTab, setActiveTab] = useState('home');

  useEffect(() => {
    async function fetchData() {
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${SHEET_ID}/values/${encodeURIComponent(
        RANGE
      )}?key=${API_KEY}`;

      try {
        const response = await axios.get(url);
        const values = response.data.values[0];

        setData({
          currentValue: values[0],
          totalReturns: values[1],
          returnsPercent: values[2],
          invested: values[3],
          xirr: values[4],
          dayReturn: values[5],
          dayReturnPercent: values[6],
        });
      } catch (error) {
        console.error('Error fetching Google Sheet data:', error);
      }
    }

    fetchData();
  }, []);

  const iconClass = (tabName) =>
    `text-xl md:text-2xl transition-colors duration-200 ${
      activeTab === tabName ? 'text-white' : 'text-purple-300 hover:text-white'
    }`;

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-600 via-purple-700 to-purple-900 text-white px-4 py-6 pb-28">
      <div className="max-w-xl mx-auto">
        {/* Header */}
        <div className="flex items-center mb-6 space-x-3">
          <img src="/assets/1fi-logo.png" alt="1Fi Logo" className="w-10 h-10" />
          <h1 className="text-2xl font-bold tracking-tight">Invest</h1>
        </div>

        {/* Current Value */}
        <div className="text-center space-y-2 mb-6">
          <p className="text-base font-medium text-purple-200">Current Value</p>
          <h2 className="text-4xl font-extrabold tracking-tight">₹ {data.currentValue}</h2>
          <p className="text-sm text-gray-200">
            Total Returns ↑ ₹ {data.totalReturns}{' '}
            <span
              className={`ml-1 ${
                parseFloat(data.returnsPercent) >= 0
                  ? 'text-green-300'
                  : 'text-red-400'
              }`}
            >
              ({parseFloat(data.returnsPercent) >= 0 ? '+' : ''}
              {data.returnsPercent})
            </span>
          </p>
        </div>

        {/* Stats Summary */}
        <div className="grid grid-cols-3 gap-3 mb-6">
          {[
            { label: 'Invested', value: `₹ ${data.invested}` },
            { label: 'XIRR', value: `${data.xirr}` },
            { label: '1 Day Return', value: `₹ ${data.dayReturn} (${data.dayReturnPercent})` },
          ].map((item, i) => (
            <div key={i} className="bg-white/10 p-4 rounded-lg shadow text-center">
              <p className="text-xs text-gray-300">{item.label}</p>
              <p className="text-sm font-semibold text-white">{item.value}</p>
            </div>
          ))}
        </div>

        {/* Chart */}
        <div className="bg-white p-4 rounded-lg mb-6 shadow">
          <img
            src="assets/investment-graph.png"
            alt="Investment Chart"
            className="w-full h-auto rounded"
          />
          <div className="flex justify-between text-xs text-gray-600 mt-3">
            {['1D', '1W', '3M', '6M', '1Y', '5Y', 'All'].map((period, i) => (
              <span key={i} className={period === 'All' ? 'font-bold text-black' : ''}>
                {period}
              </span>
            ))}
          </div>
        </div>

        {/* Actions */}
        <button className="w-full bg-green-500 hover:bg-green-600 text-white py-3 rounded-xl font-bold mb-3 transition">
          + Invest more
        </button>

        <button className="w-full bg-purple-500 hover:bg-purple-600 text-white py-3 rounded-xl text-sm font-medium mb-6 transition">
          💳 Get loan against your investments
        </button>

        {/* Portfolio Options */}
        <div className="flex justify-between items-center gap-2">
          {[
            { icon: <FaChartPie />, label: 'Portfolio' },
            { icon: <FaUndo />, label: 'Redeem' },
            { icon: <FaReceipt />, label: 'Transactions' },
          ].map((btn, idx) => (
            <button
              key={idx}
              className="flex-1 flex items-center justify-center gap-2 bg-white text-purple-800 px-3 py-2 rounded-full text-sm font-semibold shadow hover:bg-purple-100 transition"
            >
              {btn.icon}
              {btn.label}
            </button>
          ))}
        </div>
      </div>

      {/* Bottom Navigation */}
      <div className="fixed bottom-0 inset-x-0 bg-purple-800 py-3 px-8 flex justify-between items-center shadow-inner z-50 rounded-t-xl">
        <FaHome className={iconClass('home')} onClick={() => setActiveTab('home')} />
        <FaChartLine className={iconClass('stats')} onClick={() => setActiveTab('stats')} />
        <FaWallet className={iconClass('wallet')} onClick={() => setActiveTab('wallet')} />
        <FaUser className={iconClass('profile')} onClick={() => setActiveTab('profile')} />
      </div>
    </div>
  );
}

export default App;
