import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaChartLine, FaWallet, FaUser, FaHome, FaChartPie, FaUndo, FaReceipt } from 'react-icons/fa';

const SHEET_ID = '1eSQ0m5OCLYud_JBhkW26v-geKuCMOxEM-FCjIBxuuAw';
const API_KEY = 'AIzaSyAnWS12-i37cqFBWIqfHA8XASrU-0ahD80';
const RANGE = 'Sheet1!A2:G2'; // adjust if needed

function App() {
  const [data, setData] = useState({
    currentValue: '-',
    totalReturns: '-',
    returnsPercent: '-',
    invested: '-',
    xirr: '-',
    dayReturn: '-',
    dayReturnPercent: '-',
  });

  useEffect(() => {
    async function fetchData() {
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${SHEET_ID}/values/${encodeURIComponent(
        RANGE
      )}?key=${API_KEY}`;

      try {
        const response = await axios.get(url);
        const values = response.data.values[0];

        setData({
          currentValue: values[0],
          totalReturns: values[1],
          returnsPercent: values[2],
          invested: values[3],
          xirr: values[4],
          dayReturn: values[5],
          dayReturnPercent: values[6],
        });
      } catch (error) {
        console.error('Error fetching Google Sheet data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-700 to-purple-900 text-white font-sans px-4 py-6 pb-24">
      <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="flex items-center space-x-4 mb-6">
          <img src="/assets/1fi-logo.png" alt="1Fi Logo" className="w-10 h-10 rounded" />
          <h1 className="text-lg font-bold">Invest</h1>
        </div>

        {/* Current Value */}
        <div className="text-center mb-4">
          <p className="text-xlg">Current Value</p>
          <h2 className="text-4xl font-extrabold">â‚¹ {data.currentValue}</h2>
          <p className="text-sm">
            <span className="text-white">
              Total Returns â†‘ â‚¹ {data.totalReturns}
            </span>{' '}
            {(() => {
              const percentStr = data.returnsPercent?.toString().replace('%', '');
              const percent = parseFloat(percentStr);
              const isPositive = percent >= 0;

              return (
                <span className={isPositive ? 'text-green-300' : 'text-red-400'}>
                  ({isPositive ? '+' : '-'}
                  {Math.abs(percent)}%)
                </span>
              );
            })()}
          </p>
        </div>

        <div className="grid grid-cols-3 gap-3 text-center mb-4">
          <div className="bg-white bg-opacity-10 p-3 rounded-xl shadow-sm">
            <p className="text-sm text-gray-200">Invested</p>
            <p className="font-bold text-white">â‚¹ {data.invested}</p>
          </div>
          <div className="bg-white bg-opacity-10 p-3 rounded-xl shadow-sm">
            <p className="text-sm text-gray-200">XIRR</p>
            <p className="font-bold text-white">{data.xirr}</p>
          </div>
          <div className="bg-white bg-opacity-10 p-3 rounded-xl shadow-sm">
            <p className="text-sm text-gray-200">1 Day Return</p>
            <p className="font-bold text-white">â‚¹ {data.dayReturn} ({data.dayReturnPercent})</p>
          </div>
        </div>

        {/* Chart */}
        <div className="bg-white rounded-xl py-6 px-4 text-black text-center mb-4">
          <div className="flex justify-center">
            <img
              src="assets/investment-graph.png"
              alt="Investment Growth Chart"
              className="max-w-xs sm:max-w-sm md:max-w-md w-full h-auto rounded-lg"
            />
          </div>
          <div className="flex justify-around mt-2 text-xs text-gray-600">
            <span>1D</span><span>1W</span><span>3M</span><span>6M</span><span>1Y</span><span>5Y</span><span className="font-bold text-black">All</span>
          </div>
        </div>

        {/* Invest More */}
        <button className="w-full bg-green-500 text-white py-3 rounded-xl text-lg font-bold mb-4">
          + Invest more
        </button>

        {/* Loan Box */}
        <button className="w-full bg-purple-400 text-white py-3 rounded-xl text-sm font-medium mb-4">
          ðŸ’³ Get loan against your investments
        </button>

        {/* Portfolio Options */}
        <div className="flex justify-center gap-3">
          <button className="flex items-center gap-2 bg-white text-purple-800 px-4 py-2 rounded-full shadow-md text-sm font-semibold hover:bg-purple-100 transition">
            <FaChartPie /> Portfolio
          </button>
          <button className="flex items-center gap-2 bg-white text-purple-800 px-4 py-2 rounded-full shadow-md text-sm font-semibold hover:bg-purple-100 transition">
            <FaUndo /> Redeem
          </button>
          <button className="flex items-center gap-2 bg-white text-purple-800 px-4 py-2 rounded-full shadow-md text-sm font-semibold hover:bg-purple-100 transition">
            <FaReceipt /> Transactions
          </button>
        </div>
      </div>

      {/* Bottom Navigation */}
      <div className="fixed bottom-0 left-0 right-0 bg-purple-800 py-3 flex justify-around items-center text-white text-xl">
        <FaHome />
        <FaChartLine />
        <FaWallet />
        <FaUser />
      </div>
    </div>
  );
}

export default App;
