import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import {
  FaChartLine,
  FaWallet,
  FaUser,
  FaHome,
  FaChartPie,
  FaUndo,
  FaReceipt,
} from 'react-icons/fa';

const SHEET_ID = '1eSQ0m5OCLYud_JBhkW26v-geKuCMOxEM-FCjIBxuuAw';
const API_KEY = 'AIzaSyAnWS12-i37cqFBWIqfHA8XASrU-0ahD80';
const RANGE = 'Sheet1!A2:G2';

// Reusable small component for stat cards
function StatCard({ label, value }) {
  return (
    <div className="bg-white bg-opacity-10 p-4 rounded-xl text-center shadow">
      <p className="text-sm text-gray-200">{label}</p>
      <p className="text-white text-lg font-semibold">{value}</p>
    </div>
  );
}

function App() {
  const [data, setData] = useState({
    currentValue: '-',
    totalReturns: '-',
    returnsPercent: '-',
    invested: '-',
    xirr: '-',
    dayReturn: '-',
    dayReturnPercent: '-',
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('home');

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setError(null);
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${SHEET_ID}/values/${encodeURIComponent(
        RANGE
      )}?key=${API_KEY}`;

      try {
        const response = await axios.get(url);
        const values = response.data.values?.[0];

        if (!values || values.length < 7) {
          throw new Error('Invalid data format received from API');
        }

        setData({
          currentValue: values[0],
          totalReturns: values[1],
          returnsPercent: values[2],
          invested: values[3],
          xirr: values[4],
          dayReturn: values[5],
          dayReturnPercent: values[6],
        });
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load data. Please try again later.');
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  // Helper to parse and style percentage values
  const renderPercent = (percentStr) => {
    if (!percentStr) return null;
    const percent = parseFloat(percentStr.replace('%', ''));
    if (isNaN(percent)) return null;
    const isPositive = percent >= 0;
    return (
      <span className={isPositive ? 'text-green-400' : 'text-red-500'}>
        ({isPositive ? '+' : '-'}
        {Math.abs(percent)}%)
      </span>
    );
  };

  // Reusable icon class generator
  const iconClass = useCallback(
    (tabName) =>
      `text-xl sm:text-2xl transition-colors duration-200 cursor-pointer ${
        activeTab === tabName ? 'text-white' : 'text-purple-300 hover:text-white'
      }`,
    [activeTab]
  );

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-b from-purple-700 to-purple-900 text-white font-inter px-4 pt-6 pb-20">
      <div className="flex-grow max-w-3xl mx-auto space-y-6">
        {/* Header */}
        <header className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <img src="/assets/1fi-logo.png" alt="1Fi Logo" className="w-10 h-10 rounded" />
            <h1 className="text-2xl font-bold">Invest</h1>
          </div>
        </header>

        {/* Content */}
        {loading ? (
          <p className="text-center text-lg">Loading data...</p>
        ) : error ? (
          <p className="text-center text-red-500">{error}</p>
        ) : (
          <>
            {/* Current Value */}
            <section className="text-center mb-6">
              <p className="text-xl">Current Value</p>
              <h2 className="text-5xl font-extrabold">₹ {data.currentValue}</h2>
              <p className="text-base mt-1">
                <span className="text-white font-medium">Total Returns ↑ ₹ {data.totalReturns}</span>{' '}
                {renderPercent(data.returnsPercent)}
              </p>
            </section>

            {/* Stat Cards */}
            <section className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <StatCard label="Invested" value={`₹ ${data.invested}`} />
              <StatCard label="XIRR" value={data.xirr} />
              <StatCard
                label="1 Day Return"
                value={`₹ ${data.dayReturn} (${data.dayReturnPercent})`}
              />
            </section>

            {/* Chart */}
            <section className="bg-white rounded-xl py-4 px-3 text-black shadow-md">
              <img
                src="/assets/investment-graph.png"
                alt="Investment Chart"
                className="w-full max-w-md mx-auto h-auto rounded-lg"
              />
              <div className="flex justify-around mt-3 text-xs sm:text-sm text-gray-600">
                {['1D', '1W', '3M', '6M', '1Y', '5Y', 'All'].map((label) => (
                  <span
                    key={label}
                    className={`cursor-pointer ${label === 'All' ? 'font-bold text-black' : ''}`}
                  >
                    {label}
                  </span>
                ))}
              </div>
            </section>

            {/* Actions */}
            <button className="w-full bg-green-500 hover:bg-green-600 py-3 rounded-xl text-lg font-semibold transition">
              + Invest More
            </button>
            <button className="w-full bg-purple-500 hover:bg-purple-600 py-3 rounded-xl text-sm font-medium mt-2 transition">
              💳 Get Loan Against Investments
            </button>

            {/* Portfolio Options */}
            <section className="flex flex-wrap justify-center gap-4 mt-4">
              {[
                { icon: <FaChartPie />, label: 'Portfolio' },
                { icon: <FaUndo />, label: 'Redeem' },
                { icon: <FaReceipt />, label: 'Transactions' },
              ].map(({ icon, label }) => (
                <button
                  key={label}
                  className="flex items-center gap-2 bg-white text-purple-800 px-5 py-2 rounded-full shadow hover:bg-purple-100 transition"
                >
                  {icon} {label}
                </button>
              ))}
            </section>
          </>
        )}
      </div>

      {/* Bottom Navigation */}
      <nav className="fixed bottom-0 left-0 right-0 bg-purple-800 py-3 flex justify-around items-center shadow-inner">
        <FaHome
          className={iconClass('home')}
          onClick={() => setActiveTab('home')}
          title="Home"
        />
        <FaChartLine
          className={iconClass('stats')}
          onClick={() => setActiveTab('stats')}
          title="Stats"
        />
        <FaWallet
          className={iconClass('wallet')}
          onClick={() => setActiveTab('wallet')}
          title="Wallet"
        />
        <FaUser
          className={iconClass('profile')}
          onClick={() => setActiveTab('profile')}
          title="Profile"
        />
      </nav>
    </div>
  );
}

export default App;
