import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaChartLine,
  FaWallet,
  FaUser,
  FaHome,
  FaChartPie,
  FaUndo,
  FaReceipt,
} from 'react-icons/fa';

const SHEET_ID = '1eSQ0m5OCLYud_JBhkW26v-geKuCMOxEM-FCjIBxuuAw';
const API_KEY = 'AIzaSyAnWS12-i37cqFBWIqfHA8XASrU-0ahD80';
const RANGE = 'Sheet1!A2:G2';

function App() {
  const [data, setData] = useState({
    currentValue: '-',
    totalReturns: '-',
    returnsPercent: '-',
    invested: '-',
    xirr: '-',
    dayReturn: '-',
    dayReturnPercent: '-',
  });

  const [activeTab, setActiveTab] = useState('home');

  useEffect(() => {
    async function fetchData() {
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${SHEET_ID}/values/${encodeURIComponent(RANGE)}?key=${API_KEY}`;

      try {
        const response = await axios.get(url);
        const values = response.data.values[0];

        setData({
          currentValue: values[0],
          totalReturns: values[1],
          returnsPercent: values[2],
          invested: values[3],
          xirr: values[4],
          dayReturn: values[5],
          dayReturnPercent: values[6],
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  const iconClass = (tabName) =>
    `text-xl sm:text-2xl transition-colors duration-200 ${
      activeTab === tabName ? 'text-white' : 'text-purple-300 hover:text-white'
    }`;

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-700 to-purple-900 text-white font-inter px-4 pt-6 pb-24">
      <div className="max-w-3xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <img src="/assets/1fi-logo.png" alt="1Fi Logo" className="w-10 h-10 rounded" />
            <h1 className="text-2xl font-bold">Invest</h1>
          </div>
        </div>

       {/* Current Value */}
        <div className="text-center mb-6">
          <p className="text-xl">Current Value</p>
          <h2 className="text-5xl font-extrabold">â‚¹ {data.currentValue}</h2>
          <p className="text-base mt-1">
            <span className="text-white font-medium">
              Total Returns â†‘ â‚¹ {data.totalReturns}
            </span>{' '}
            {(() => {
              const percentStr = data.returnsPercent?.toString().replace('%', '');
              const percent = parseFloat(percentStr);
              const isPositive = percent >= 0;

              return (
                <span className={isPositive ? 'text-green-400' : 'text-red-500'}>
                  ({isPositive ? '+' : '-'}
                  {Math.abs(percent)}%)
                </span>
              );
            })()}
          </p>
        </div>


        {/* Stat Cards */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          {[
            { label: 'Invested', value: `â‚¹ ${data.invested}` },
            { label: 'XIRR', value: data.xirr },
            { label: '1 Day Return', value: `â‚¹ ${data.dayReturn} (${data.dayReturnPercent})` },
          ].map((item, idx) => (
            <div key={idx} className="bg-white bg-opacity-10 p-4 rounded-xl text-center shadow">
              <p className="text-sm text-gray-200">{item.label}</p>
              <p className="text-white text-lg font-semibold">{item.value}</p>
            </div>
          ))}
        </div>

        {/* Chart */}
        <div className="bg-white rounded-xl py-4 px-3 text-black shadow-md">
          <img
            src="/assets/investment-graph.png"
            alt="Investment Chart"
            className="w-full max-w-md mx-auto h-auto rounded-lg"
          />
          <div className="flex justify-around mt-3 text-xs sm:text-sm text-gray-600">
            {['1D', '1W', '3M', '6M', '1Y', '5Y', 'All'].map((label, idx) => (
              <span
                key={idx}
                className={`cursor-pointer ${label === 'All' ? 'font-bold text-black' : ''}`}
              >
                {label}
              </span>
            ))}
          </div>
        </div>

        {/* Actions */}
        <button className="w-full bg-green-500 hover:bg-green-600 py-3 rounded-xl text-lg font-semibold">
          + Invest More
        </button>
        <button className="w-full bg-purple-500 hover:bg-purple-600 py-3 rounded-xl text-sm font-medium">
          ðŸ’³ Get Loan Against Investments
        </button>

        {/* Portfolio Options */}
        <div className="flex flex-wrap justify-center gap-4">
          {[
            { icon: <FaChartPie />, label: 'Portfolio' },
            { icon: <FaUndo />, label: 'Redeem' },
            { icon: <FaReceipt />, label: 'Transactions' },
          ].map((item, idx) => (
            <button
              key={idx}
              className="flex items-center gap-2 bg-white text-purple-800 px-5 py-2 rounded-full shadow hover:bg-purple-100 transition"
            >
              {item.icon} {item.label}
            </button>
          ))}
        </div>
      </div>

      {/* Bottom Navigation */}
      <div className="fixed bottom-0 left-0 right-0 bg-purple-800 py-3 flex justify-around items-center shadow-inner">
        <FaHome className={iconClass('home')} onClick={() => setActiveTab('home')} title="Home" />
        <FaChartLine className={iconClass('stats')} onClick={() => setActiveTab('stats')} title="Stats" />
        <FaWallet className={iconClass('wallet')} onClick={() => setActiveTab('wallet')} title="Wallet" />
        <FaUser className={iconClass('profile')} onClick={() => setActiveTab('profile')} title="Profile" />
      </div>
    </div>
  );
}

export default App;
