import React from 'react';
import {
  Box,
  Typography,
  Grid,
  Container,
  Paper,
  Button,
} from '@mui/material';
import CurrencyRupeeIcon from '@mui/icons-material/CurrencyRupee';

const App = () => {
  const data = {
    currentValue: '2,56,700',
    totalReturns: '56,700',
    returnsPercent: '28.3%',
    invested: '2,00,000',
    xirr: '13.2%',
    dayReturn: '450',
    dayReturnPercent: '0.22%',
  };

  return (
    <Container maxWidth="sm" disableGutters>
      {/* Gradient Header */}
      <Box
        sx={{
          background: 'linear-gradient(180deg, #7b2ff7, #f107a3)',
          color: 'white',
          p: 3,
          borderTopLeftRadius: '0px',
          borderTopRightRadius: '0px',
          textAlign: 'center',
        }}
      >
        <Typography variant="body2" gutterBottom>Current Value</Typography>
        <Typography variant="h4" fontWeight="bold">
          ₹ {data.currentValue}
        </Typography>
        <Typography variant="body1" mt={1}>
          Total Returns{' '}
          <span style={{ color: '#00ff99', fontWeight: 600 }}>
            ↑ ₹ {data.totalReturns} ({data.returnsPercent})
          </span>
        </Typography>
      </Box>

      {/* White Card with Stats */}
      <Paper
        elevation={4}
        sx={{
          mx: 2,
          mt: -4,
          mb: 2,
          borderRadius: 3,
          overflow: 'hidden',
        }}
      >
        <Box sx={{ p: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={4} textAlign="center">
              <Typography variant="body2">Invested</Typography>
              <Typography variant="subtitle1" fontWeight={600}>
                ₹ {data.invested}
              </Typography>
            </Grid>
            <Grid item xs={4} textAlign="center">
              <Typography variant="body2">XIRR</Typography>
              <Typography variant="subtitle1" fontWeight={600}>
                {data.xirr}
              </Typography>
            </Grid>
            <Grid item xs={4} textAlign="center">
              <Typography variant="body2">1 Day Return</Typography>
              <Typography variant="subtitle1" fontWeight={600}>
                ₹ {data.dayReturn} ({data.dayReturnPercent})
              </Typography>
            </Grid>
          </Grid>
        </Box>

        {/* Action Buttons */}
        <Box sx={{ px: 2, pb: 2 }}>
          <Button variant="contained" color="success" fullWidth sx={{ mb: 1 }}>
            + Invest more
          </Button>
          <Button variant="contained" fullWidth sx={{ background: '#7B2FF7' }}>
            Get loan against your investments
          </Button>
        </Box>

        {/* Bottom Options */}
        <Box display="flex" justifyContent="space-around" py={2}>
          <Button variant="outlined">Portfolio</Button>
          <Button variant="outlined">Redeem</Button>
          <Button variant="outlined">Transactions</Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default App;
