import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaChartLine,
  FaWallet,
  FaUser,
  FaHome,
  FaChartPie,
  FaUndo,
  FaReceipt,
} from 'react-icons/fa';

const SHEET_ID = '1eSQ0m5OCLYud_JBhkW26v-geKuCMOxEM-FCjIBxuuAw';
const API_KEY = 'AIzaSyAnWS12-i37cqFBWIqfHA8XASrU-0ahD80';
const RANGE = 'Sheet1!A2:G2'; // adjust if needed

function App() {
  const [data, setData] = useState({
    currentValue: '-',
    totalReturns: '-',
    returnsPercent: '-',
    invested: '-',
    xirr: '-',
    dayReturn: '-',
    dayReturnPercent: '-',
  });

  const [activeTab, setActiveTab] = useState('home');

  useEffect(() => {
    async function fetchData() {
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${SHEET_ID}/values/${encodeURIComponent(
        RANGE
      )}?key=${API_KEY}`;

      try {
        const response = await axios.get(url);
        const values = response.data.values[0];

        setData({
          currentValue: values[0],
          totalReturns: values[1],
          returnsPercent: values[2],
          invested: values[3],
          xirr: values[4],
          dayReturn: values[5],
          dayReturnPercent: values[6],
        });
      } catch (error) {
        console.error('Error fetching Google Sheet data:', error);
      }
    }

    fetchData();
  }, []);

  const navItems = [
    { name: 'home', label: 'Home', icon: FaHome },
    { name: 'stats', label: 'Stats', icon: FaChartLine },
    { name: 'wallet', label: 'Wallet', icon: FaWallet },
    { name: 'profile', label: 'Profile', icon: FaUser },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-700 to-purple-900 text-white font-sans px-6 py-6">
      {/* Centered top container */}
      <div className="max-w-xl mx-auto">
        {/* Header */}
        <div className="flex items-center space-x-4 mb-6">
          <img src="/assets/1fi-logo.png" alt="1Fi Logo" className="w-12 h-12 rounded" />
          <h1 className="text-2xl font-bold">Invest</h1>
        </div>

        {/* Top Navigation - Desktop style */}
        <nav className="flex space-x-10 mb-8 border-b border-purple-600 pb-3 justify-center">
          {navItems.map(({ name, label, icon: Icon }) => (
            <button
              key={name}
              onClick={() => setActiveTab(name)}
              className={`flex items-center space-x-2 px-3 py-2 rounded-md transition-colors
                ${
                  activeTab === name
                    ? 'bg-white text-purple-900 font-semibold shadow'
                    : 'text-purple-300 hover:text-white hover:bg-purple-600'
                }`}
              aria-current={activeTab === name ? 'page' : undefined}
            >
              <Icon className="text-xl" />
              <span className="text-sm">{label}</span>
            </button>
          ))}
        </nav>

        {/* Current Value */}
        <div className="text-center mb-6">
          <p className="text-xl">Current Value</p>
          <h2 className="text-5xl font-extrabold">₹ {data.currentValue}</h2>
          <p className="text-base mt-1">
            <span className="text-white font-medium">
              Total Returns ↑ ₹ {data.totalReturns}
            </span>{' '}
            {(() => {
              const percentStr = data.returnsPercent?.toString().replace('%', '');
              const percent = parseFloat(percentStr);
              const isPositive = percent >= 0;

              return (
                <span className={isPositive ? 'text-green-400' : 'text-red-500'}>
                  ({isPositive ? '+' : '-'}
                  {Math.abs(percent)}%)
                </span>
              );
            })()}
          </p>
        </div>

        {/* Summary Grid */}
        <div className="grid grid-cols-3 gap-6 text-center mb-8">
          <div className="bg-white bg-opacity-10 p-4 rounded-xl shadow-md">
            <p className="text-sm text-gray-300">Invested</p>
            <p className="font-bold text-white text-lg">₹ {data.invested}</p>
          </div>
          <div className="bg-white bg-opacity-10 p-4 rounded-xl shadow-md">
            <p className="text-sm text-gray-300">XIRR</p>
            <p className="font-bold text-white text-lg">{data.xirr}</p>
          </div>
          <div className="bg-white bg-opacity-10 p-4 rounded-xl shadow-md">
            <p className="text-sm text-gray-300">1 Day Return</p>
            <p className="font-bold text-white text-lg">
              ₹ {data.dayReturn} ({data.dayReturnPercent})
            </p>
          </div>
        </div>
      </div>

      {/* Wide lower container */}
      <div className="max-w-6xl mx-auto px-6">
        {/* Chart */}
        <div className="bg-white rounded-xl py-8 px-6 text-black text-center mb-8 shadow-lg">
          <div className="flex justify-center">
            <img
              src="assets/investment-graph.png"
              alt="Investment Growth Chart"
              className="max-w-full w-full h-auto rounded-lg max-h-[400px]"
            />
          </div>
          <div className="flex justify-center space-x-8 mt-4 text-sm text-gray-600 font-semibold">
            <span className="cursor-pointer hover:text-purple-700">1D</span>
            <span className="cursor-pointer hover:text-purple-700">1W</span>
            <span className="cursor-pointer hover:text-purple-700">3M</span>
            <span className="cursor-pointer hover:text-purple-700">6M</span>
            <span className="cursor-pointer hover:text-purple-700">1Y</span>
            <span className="cursor-pointer hover:text-purple-700">5Y</span>
            <span className="cursor-default text-purple-900 font-bold">All</span>
          </div>
        </div>

        {/* Invest More */}
        <button className="w-full bg-green-500 text-white py-4 rounded-xl text-lg font-bold mb-6 hover:bg-green-600 transition">
          + Invest more
        </button>

        {/* Loan Box */}
        <button className="w-full bg-purple-500 text-white py-3 rounded-xl text-sm font-medium mb-6 hover:bg-purple-600 transition">
          💳 Get loan against your investments
        </button>

        {/* Portfolio Options */}
        <div className="flex justify-center gap-6 mb-12">
          <button className="flex items-center gap-3 bg-white text-purple-900 px-5 py-3 rounded-full shadow-md text-base font-semibold hover:bg-purple-100 transition">
            <FaChartPie className="text-lg" /> Portfolio
          </button>
          <button className="flex items-center gap-3 bg-white text-purple-900 px-5 py-3 rounded-full shadow-md text-base font-semibold hover:bg-purple-100 transition">
            <FaUndo className="text-lg" /> Redeem
          </button>
          <button className="flex items-center gap-3 bg-white text-purple-900 px-5 py-3 rounded-full shadow-md text-base font-semibold hover:bg-purple-100 transition">
            <FaReceipt className="text-lg" /> Transactions
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
